mapper.select:
/* selectFileInfo - 파일 조회 */
SELECT B.ALC_NO, A.FILE_NO, A.FILE_PATH, A.FILE_NM, A.FILE_EXT
  FROM ALC_FILE A
     , INFORMATION B
 WHERE A.FILE_NO = B.FILE_NO
   AND B.ALC_NO = #{alcNo}

mapper.update:
UPDATE INFORMATION
   SET ALC_NM = #{alcNm}
     , CATE_NO = #{cateNo}
     , VOL = #{vol}
     , PRODUCTOR = #{productor}
     , DETAIL = #{detail}
     , FILE_NO = #{fileNo}
     , EXP_YN = #{expYn}
 WHERE ALC_NO = #{alcNo}

UPDATE INFORMATION
   SET EXP_YN = #{expYn}
 WHERE ALC_NO = #{alcNo}

/* updateAlcFileInfo - 파일 등록 - 술에 파일 정보 업데이트 */
UPDATE INFORMATION
   SET FILE_NO = #{fileNo}
 WHERE ALC_NO = #{alcNo}
